function setActive(b){$("#navbar").children("li").removeClass("active");$(b).addClass("active")}function showFile(b,a){$("#working").empty();newFileName=b+".php";$("#working").load(newFileName);$("#"+a).siblings("button").removeClass("active");$("#"+a).addClass("active")}
function showModal(b,a){var c=$("#detailModal");switch(a){case "ce":a="courseEdit.php";break;case "le":a="learnerDetailDisplay.php";break;default:a="detailDisplay.php"}c.html("");$.ajax({url:a,data:{recId:b},type:"POST",success:function(a){c.html(a)}})}function logout(){alert("Logging Out");window.location.href="http://admin.inspiredinterfaces.co.za"}
function addLearner(b){alert("addLearner - "+b);$("#rightHalf").html("");$.ajax({url:"learnerInsert.php",data:{UserName:$("#Email").val(),pwd:$("#pwd").val(),FName:$("#FName").val(),SName:$("#SName").val(),SAIDNo:$("#SAIDNo").val(),Email:$("#Email").val(),CompanyId:$("#CompanyId").val(),CourseId:$("#CourseId").val()},type:"POST",success:function(a){alert("Posted");$("#rightHalf").html(a);return!0}})}
function addCourse(){$("#btnSubmit").prop("disabled","disabled");$.ajax({url:"courseInsert.php",data:{Code:$("#Code").val(),Description:$("#Description").val()},type:"POST",success:function(b){$("#rightCol").html(b)},complete:function(){$("#btnCourseList").click()},error:function(){alert("There was an error saving your new course...");$("#btnCourseNew").click()}})}
function checkExists(b,a){$.ajax({url:"checker.php",data:{fieldId:b,fieldValue:a},type:"POST",success:function(c){fullFieldId="#"+b;if(1==c)return alert(a+" already exists in the database."),$("#rightHalf").html("<br><br><p>"+b+" already exists with the value "+a+"</p>"),$(fullFieldId).select(),!0;$("#rightHalf").html("");return!1}})}
function check_submit(b,a){0==$("#Email").val().length||0==$("#FName").val().length||0==$("#SName").val().length||0==$("#SAIDNo").val().length||"zzzz"===$("#CompanyId").val()||"zzzz"===$("#CourseId").val()?$("#btnSubmit").attr("disabled",!0):$("#btnSubmit").removeAttr("disabled")};